/*************************************************************************** %gsw_specvol_p_partsspecificvolumepressureterms %(75-termequation) %========================================================================== % %USAGE:gsw_specvol_p_parts(SA,CT,*vp0,*vp1,*vp2,*vp3,*vp4,*vp5, *vp6) % %DESCRIPTION: %CalculateseachofthepressuretermsinspecificvolumefromAbsolute %SalinityandConservativeTemperature,usingthecomputationally %efficient75-termpolynomialexpressionforspecificvolume(Roquetet %al.,2015). % %Notethatthe75-termequationhasbeenfittedinarestrictedrangeof %parameterspace,andismostaccurateinsidethe"oceanographicfunnel" %describedinMcDougalletal.(2003).TheGSWlibraryfunction %"gsw_infunnel(SA,CT,p)"isavailabletobeusedifonewantstotestif %someofone|sdataliesoutsidethis"funnel". % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] % %OUTPUT: %vp0=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe0 %vp1=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe1 %vp2=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe2 %vp3=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe3 %vp4=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe4 %vp5=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe5 %vp6=AbsoluteSalinityandConservativeTemperaturetermsforpressure %tothe6 % %AUTHOR: %FabienRoquet,GurvanMadec,TrevorMcDougall&PaulBarker %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org. % %McDougall,T.J.,D.R.Jackett,D.G.WrightandR.Feistel,2003: %Accurateandcomputationallyefficientalgorithmsforpotential %temperatureanddensityofseawater.J.Atmos.Ocean.Tech.,20, %730-741. % %Roquet,F.,G.Madec,T.J.McDougall,P.M.Barker,2015:Accurate %polynomialexpressionsforthedensityandspecifcvolumeofseawater %usingtheTEOS-10standard.OceanModelling,90,pp.29-43. % %Thissoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ voidTeosBase::gsw_specvol_p_parts( doubleSA,doubleCT,double&vp0,double&vp1, double&vp2,double&vp3,double&vp4,double&vp5, double&vp6) { doubleys,xs,x2,sa=0.0;
if(SA>0.0){sa=SA;}
x2=gtc.gsw_sfac*sa; xs=sqrt(x2+gtc.offset); ys=CT*0.025;
vp0=gsvco.v000+xs*(gsvco.v100+xs*(gsvco.v200+xs*(gsvco.v300+ xs*(gsvco.v400+xs*(gsvco.v500+xs*gsvco.v600)))))+ ys*(gsvco.v010+xs*(gsvco.v110+xs*(gsvco.v210+ xs*(gsvco.v310+xs*(gsvco.v410+xs*gsvco.v510))))+ ys*(gsvco.v020+xs*(gsvco.v120+xs*(gsvco.v220+ xs*(gsvco.v320+xs*gsvco.v420)))+ ys*(gsvco.v030+xs*(gsvco.v130+xs*(gsvco.v230+ xs*gsvco.v330))+ys*(gsvco.v040+xs*(gsvco.v140+xs*gsvco.v240)+ ys*(gsvco.v050+xs*gsvco.v150+ys*gsvco.v060)))));
vp1=gsvco.v001+xs*(gsvco.v101+xs*(gsvco.v201+ xs*(gsvco.v301+xs*(gsvco.v401+xs*gsvco.v501))))+ ys*(gsvco.v011+xs*(gsvco.v111+xs*(gsvco.v211+ xs*(gsvco.v311+xs*gsvco.v411)))+ ys*(gsvco.v021+xs*(gsvco.v121+xs*(gsvco.v221+xs*gsvco.v321))+ ys*(gsvco.v031+xs*(gsvco.v131+xs*gsvco.v231)+ ys*(gsvco.v041+xs*gsvco.v141+ys*gsvco.v051))));
vp2=gsvco.v002+xs*(gsvco.v102+xs*(gsvco.v202+xs*(gsvco.v302+xs*gsvco.v402)))+ ys*(gsvco.v012+xs*(gsvco.v112+xs*(gsvco.v212+xs*gsvco.v312))+ ys*(gsvco.v022+xs*(gsvco.v122+xs*gsvco.v222)+ ys*(gsvco.v032+xs*gsvco.v132+ys*gsvco.v042)));
vp3=gsvco.v003+xs*(gsvco.v103+xs*gsvco.v203)+ ys*(gsvco.v013+xs*gsvco.v113+ ys*gsvco.v023);
vp4=gsvco.v004+xs*gsvco.v104+ys*gsvco.v014;
vp5=gsvco.v005;
vp6=gsvco.v006;
//Notethat, //specvol=vp0+z.*(vp1+z.*(vp2+z.*(vp3 //+z.*(vp4+z.*(vp5+z.*vp6))))); }
/*************************************************************************** %gsw_t90_from_t68ITS-90temperaturefromIPTS-68temperature %========================================================================== % %USAGE:gsw_t90_from_t68(t68) % %DESCRIPTION: %ConvertsIPTS-68temperaturetoInternationalTemperatureScale1990 %(ITS-90)temperature.Thisconversionshouldbeappliedtoallin-situ %datacollectedbetween1/1/1968and31/12/1989. % %INPUT: %t68=in-situtemperature(IPTS-68)[degC] % %OUTPUT: %t90=in-situtemperature(ITS-90)[degC] % %AUTHOR: %PaulBarkerandTrevorMcDougall[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %InternationalTemperatureScalesof1948,1968and1990,anICES %note,availablefromhttp://www.ices.dk/ocean/procedures/its.htm % %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org %Seesection2.1andappendixA.1ofthisTEOS-10manual. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_t90_from_t68(doublet68) { returnt68*0.999760057586179; }
/*************************************************************************** %gsw_weighted_nanmeanweightedmeanofnon-NaNvalues %========================================================================== % %USAGE:gsw_weighted_nanmean(data,weights,*weighted_mean,*weighted_weights) % %DESCRIPTION: %Thisfunctionreturnstheweightedmeanofthedata,wheretheweights %aregivenbyweightsandacorespondingweightedweightfortheweighted %mean.Iftheweightsarenotsuppliedthentheweightsareconsiderdto %beallequal.ThisprogrammetreatsNaN|sasmissingvalues. % %INPUT: %data=datavalues % %Optional %weights=weightsofthedata % %OUTPUT: %weighted_mean=weightedmeanofthedata %weighted_weights=weightedweightsoftheweights % %AUTHOR: %PaulBarkerandTrevorMcDougall,basedonnanmeanfromMatlab. % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ voidTeosBase::gsw_weighted_nanmean( doubledata,doubleweights, double&weighted_mean, double&weighted_weights) { doubleunnamed_idx_0; doubleweighted_data; intnz; boolInan;
weighted_data=weights*data; Inan=gsw_rtIsNaN(weighted_data); unnamed_idx_0=weights;
if(Inan){ weighted_data=0.0; unnamed_idx_0=0.0; }
if(unnamed_idx_0==0.0){ unnamed_idx_0=rtNaN; }
weighted_mean=weighted_data/unnamed_idx_0; nz=!Inan; weighted_data=nz;
if(nz==0){ weighted_data=rtNaN; }
weighted_weights=unnamed_idx_0/weighted_data; }
/*************************************************************************** %gsw_rho_second_derivatives_CT_exactSAandCTpartialsecond %orderderivativesofdensity %========================================================================== % %USAGE:gsw_rho_second_derivatives_CT_exact(SA,CT,p,*rho_SA_SA, *rho_SA_CT, *rho_CT_CT, *rho_SA_P, *rho_CT_P) % %DESCRIPTION: %Calculatesthefollowingfivesecond-orderderivativesofrho, %(1)rho_SA_SA,second-orderderivativewithrespecttoAbsolute %SalinityatconstantCT&p. %(2)rho_SA_CT,second-orderderivativewithrespecttoSA&CTat %constantp. %(3)rho_CT_CT,second-orderderivativewithrespecttoCTat %constantSA&p. %(4)rho_SA_P,second-orderderivativewithrespecttoSA&Pat %constantCT. %(5)rho_CT_P,second-orderderivativewithrespecttoCT&Pat %constantSA. % %NotethatthisfunctionusesthefullGibbsfunction.Thereisan %alternativetocallingthisfunction,namely %gsw_rho_second_derivatives(SA,CT,p),whichusesthecomputationally %efficientpolynomialforspecificvolumeintermsofSA,CTandp %(Roquetetal.,2015). % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) % %OUTPUT: %rho_SA_SA=Thesecond-orderderivativeofrhowithrespectto %AbsoluteSalinityatconstantCT&p.[(kg/m^3)(g/kg)^-2] %rho_SA_CT=Thesecond-orderderivativeofrhowithrespectto %SAandCTatconstantp.[(kg/m^3)(g/kg)^-1K^-1] %rho_CT_CT=Thesecond-orderderivativeofrhowithrespecttoCTat %constantSA&p[(kg/m^3)K^-2] %rho_SA_P=Thesecond-orderderivativewithrespecttoSA&Pat %constantCT.[(kg/m^3)(g/kg)^-1Pa^-1] %rho_CT_P=Thesecond-orderderivativewithrespecttoCT&Pat %constantSA.[(kg/m^3)K^-1Pa^-1] % %AUTHOR: %PaulBarkerandTrevorMcDougall[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org %SeeappendixA.20andappendixKofthisTEOS-10Manual. % %Roquet,F.,G.Madec,T.J.McDougall,P.M.Barker,2015:Accurate %polynomialexpressionsforthedensityandspecifcvolumeofseawater %usingtheTEOS-10standard.OceanModelling.,90,pp.29-43. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ voidTeosBase::gsw_rho_second_derivatives_CT_exact( doubleSA,doubleCT,doublep, double*rho_SA_SA,double*rho_SA_CT, double*rho_CT_CT,double*rho_SA_P, double*rho_CT_P) { double*v_SA_SA=newdouble,*v_SA_CT=newdouble,*v_CT_CT=newdouble; double*v_SA_P=newdouble,*v_CT_P=newdouble; double*v_SA=newdouble,*v_CT=newdouble,*v_P=newdouble; doublerec_v,rec_v2,rec_v3,sa=0.0;
if(SA>0.0){sa=SA;}
rec_v=1.0/gsw_specvol_CT_exact(sa,CT,p); gsw_specvol_first_derivatives_CT_exact(sa,CT,p,*v_SA,*v_CT,*v_P); gsw_specvol_second_derivatives_CT_exact(sa,CT,p,*v_SA_SA,*v_SA_CT,*v_CT_CT,*v_SA_P, *v_CT_P);
rec_v2=rec_v*rec_v; rec_v3=rec_v2*rec_v;
*rho_CT_CT=-*v_CT_CT*rec_v2+2.0*(*v_CT**v_CT)*rec_v3;
*rho_SA_CT=-*v_SA_CT*rec_v2+2.0**v_SA**v_CT*rec_v3;
*rho_SA_SA=-*v_SA_SA*rec_v2+2.0*(*v_SA**v_SA)*rec_v3;
*rho_SA_P=-*v_SA_P*rec_v2+2.0**v_SA**v_P*rec_v3;
*rho_CT_P=-*v_CT_P*rec_v2+2.0**v_CT**v_P*rec_v3;
deletev_SA_SA; deletev_SA_CT; deletev_CT_CT; deletev_SA_P; deletev_CT_P; deletev_SA; deletev_CT; deletev_P; }
/*************************************************************************** %gsw_specvol_diffspecificvolumedifferencebetweentwopressures %(75-termequation) %========================================================================== % %USAGE:gsw_specvol_diff(SA,CT,p_shallow,p_deep) % %DESCRIPTION: %Calculatesthedifferenceofthespecificvolumeofseawaterbetween %twodifferentpressures,p_deep(thedeeperpressure)andp_shallow %(theshallowerpressure),atthesamevaluesofSAandCT.This %functionusesthecomputationally-efficientexpressionforspecific %volumeintermsofSA,CTandp(Roquetetal.,2015).Theoutput %(specvol_diff)isthespecificvolumeevaluatedat(SA,CT,p_deep) %minusthespecificvolumeat(SA,CT,p_shallow). % %Notethatthis75-termequationhasbeenfittedinarestrictedrangeof %parameterspace,andismostaccurateinsidethe"oceanographicfunnel" %describedinMcDougalletal.(2003).TheGSWlibraryfunction %"gsw_infunnel(SA,CT,p)"isavaialbletobeusedifonewantstotestif %someofone|sdataliesoutsidethis"funnel". % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p_shallow=upperseapressure[dbar] %(i.e.shallowerabsolutepressure-10.1325dbar) %p_deep=lowerseapressure[dbar] %(i.e.deeperabsolutepressure-10.1325dbar) % %OUTPUT: %specvol_diff=differenceofspecificvolume[m^3/kg] %(deepminusshallow) % %AUTHOR: %PaulBarkerandTrevorMcDougall[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org %SeeEqn.(3.7.3)ofthisTEOS-10Manual. % %McDougall,T.J.,D.R.Jackett,D.G.WrightandR.Feistel,2003: %Accurateandcomputationallyefficientalgorithmsforpotential %temperatureanddensityofseawater.J.Atmosph.Ocean.Tech.,20, %pp.730-741. % %Roquet,F.,G.Madec,T.J.McDougall,P.M.Barker,2015:Accurate %polynomialexpressionsforthedensityandspecifcvolumeofseawater %usingtheTEOS-10standard.OceanModelling.,90,pp.29-43. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_specvol_diff( doubleSA,doubleCT,doublep_shallow, doublep_deep) { doublev011=1.8505765429e-5; doublev012=-1.1736386731e-6; doublev013=-3.6527006553e-7; doublev014=3.1454099902e-7; doublev021=-1.1716606853e-5; doublev022=2.1305028740e-6; doublev023=2.8695905159e-7; doublev031=7.9279656173e-6; doublev032=-4.6132540037e-7; doublev041=-3.4102187482e-6; doublev042=-6.3352916514e-8; doublev051=5.0736766814e-7; doublev101=2.4262468747e-5; doublev102=-5.8484432984e-7; doublev103=3.6310188515e-7; doublev104=-1.1147125423e-7; doublev121=-2.3678308361e-7; doublev122=3.9137387080e-7; doublev131=-3.4558773655e-6; doublev132=7.7618888092e-9; doublev141=1.2956717783e-6; doublev201=-3.4792460974e-5; doublev202=-4.8122251597e-6; doublev203=1.6746303780e-8; doublev211=1.1100834765e-5; doublev212=5.4620748834e-6; doublev231=3.1655306078e-7; doublev301=3.7470777305e-5; doublev302=4.9263106998e-6; doublev311=-9.8447117844e-6; doublev312=-1.3544185627e-6; doublev321=-4.8826139200e-7; doublev401=-1.7322218612e-5; doublev402=-1.7811974727e-6; doublev411=2.5909225260e-6; doublev501=3.0927427253e-6;
doubledz,z_part_2,x2,xs,ys,z_shallow,z_shallow2,z_shallow3; doublez_deep,z_deep2,z_deep_times_z_shallow,z_deep2_plus_z_shallow2; doublexy_part_1,xy_part_2,xy_part_3,xy_part_4,z_part_3,z_part_4; doublez_part_5,z_part_6; doublesa=0.0;
if(SA>0.0){sa=SA;}
x2=gtc.gsw_sfac*sa; xs=sqrt(x2+gtc.offset); ys=CT*0.025; z_shallow=p_shallow*gtc.rec_db2pa; z_deep=p_deep*gtc.rec_db2pa;
xy_part_1=gsvco.v001+xs*(v101+xs*(v201+xs*(v301+xs*(v401+v501*xs))))+ ys*(v011+xs*(gsvco.v111+xs*(v211+xs*(v311+v411*xs)))+ys*(v021+ xs*(v121+xs*(gsvco.v221+v321*xs))+ys*(v031+xs*(v131+v231*xs)+ ys*(v041+v141*xs+v051*ys))));
xy_part_2=gsvco.v002+xs*(v102+xs*(v202+xs*(v302+v402*xs)))+ ys*(v012+xs*(gsvco.v112+xs*(v212+v312*xs))+ys*(v022+xs*(v122+ gsvco.v222*xs)+ys*(v032+v132*xs+v042*ys)));
xy_part_3=gsvco.v003+xs*(v103+v203*xs)+ys*(v013+gsvco.v113*xs+v023*ys);
xy_part_4=gsvco.v004+v104*xs+v014*ys;
dz=z_deep-z_shallow;
z_part_2=z_deep+z_shallow;
z_deep2=z_deep*z_deep; z_shallow2=z_shallow*z_shallow; z_deep_times_z_shallow=z_deep*z_shallow; z_deep2_plus_z_shallow2=z_deep2+z_shallow2;
z_part_3=z_deep2_plus_z_shallow2+z_deep_times_z_shallow;
z_part_4=z_part_2*z_deep2_plus_z_shallow2;
z_shallow3=z_shallow*z_shallow2;
z_part_5=z_deep2*z_part_3+z_shallow3*z_part_2;
z_part_6=z_part_2*z_part_3*(z_deep2_plus_z_shallow2-z_deep_times_z_shallow);
returndz*(xy_part_1+z_part_2*xy_part_2+z_part_3*xy_part_3+ z_part_4*xy_part_4+z_part_5*gsvco.v005+ z_part_6*gsvco.v006); }
/*************************************************************************** %gsw_N2solsolubilityofN2inseawater %========================================================================== % %USAGE: %N2sol=gsw_N2sol(SA,CT,p,lon,lat) % %DESCRIPTION: %Calculatesthenitrogen,N2,concentrationexpectedatequilibriumwith %airatanAbsolutePressureof101325Pa(seapressureof0dbar) %includingsaturatedwatervapor.Thisfunctionusesthesolubility %coefficientsaslistedinHammeandEmerson(2004). % %NotethatthisalgorithmhasnotbeenapprovedbyIOCandisnotwork %fromSCOR/IAPSOWorkingGroup127.ItisincludedintheGSW %OceanographicToolboxasitseemstobeoceanographicbestpractice. % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) %lon=longitudeindecimaldegrees[0...+360] %or[-180...+180] %lat=latitudeindecimaldegreesnorth[-90...+90] % %OUTPUT: %N2sol=solubilityofnitrogeninmicro-molesperkg[umol/kg] % %AUTHOR:RobertaHamme,PaulBarkerandTrevorMcDougall %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %Hamme,R.,andS.Emerson,2004:Thesolubilityofneon,nitrogenand %argonindistilledwaterandseawater.Deep-SeaResearch,51, %1517-1528. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_N2sol(doubleSA,doubleCT,doublep,doublelon,doublelat) { //ThecoefficentsbelowarefromTable4ofHammeandEmerson(2004) doublea0=6.42931; doublea1=2.92704; doublea2=4.32531; doublea3=4.69149; doubleb0=-7.44129e-3; doubleb1=-8.02566e-3; doubleb2=-1.46775e-2;
//NotethatsalinityargumentisPracticalSalinity,thisis //beacusethemajorioniccomponentsofseawaterrelatedtoCl //arewhataffectthesolubilityofnon-electrolytesinseawater.
doubley,pt,N2sol,SP=gsw_sp_from_sa(SA,p,lon,lat);
pt=gsw_pt_from_ct(SA,CT);//ptispotentialtemperaturereferencedtotheseasurface.
y=log((298.15-pt)/(gtc.gsw_t0+pt));//ptisthetemperatureindegressC //onthe1990InternationalTemperatureScaleITS-90.
N2sol=exp(a0+y*(a1+y*(a2+a3*y))+SP*(b0+y*(b1+b2*y)));
returnN2sol; }
/*************************************************************************** %gsw_N2OsolsolubilityofN2Oinseawater %========================================================================== % %USAGE: %N2Osol=gsw_N2Osol(SA,CT,p,lon,lat) % %DESCRIPTION: %Calculatesthenitrousoxide,N2O,concentrationexpectedatequilibrium %withairatanAbsolutePressureof101325Pa(seapressureof0dbar) %includingsaturatedwatervapor.Thisfunctionusesthesolubility %coefficientsaslistedinWeissandPrice(1980). % %NotethatthisalgorithmhasnotbeenapprovedbyIOCandisnotwork %fromSCOR/IAPSOWorkingGroup127.ItisincludedintheGSW %OceanographicToolboxasitseemstobeoceanographicbestpractice. % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) %lon=longitudeindecimaldegrees[0...+360] %or[-180...+180] %lat=latitudeindecimaldegreesnorth[-90...+90] % %OUTPUT: %N2Osol=solubilityofnitrousoxide[mol/L] % %AUTHOR:RichPawlowicz,PaulBarkerandTrevorMcDougall %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %Weiss,R.F.,andB.A.Price,1980:Nitrousoxidesolubilityinwaterand %seawater.Mar.Chem.,8,347-359. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_N2Osol(doubleSA,doubleCT,doublep,doublelon,doublelat) { doubleph2odP,y_100,y,pt68,N2Osol,pt,SP=gsw_sp_from_sa(SA,p,lon,lat);
//ThecoefficentsbelowarefromTable2ofWeissandPrice(1980) doublea0=-165.8806; doublea1=222.8743; doublea2=92.0792; doublea3=-1.48425;
doubleb1=-0.056235; doubleb2=0.031619; doubleb3=-0.0048472;
doublem0=24.4543; doublem1=67.4509; doublem2=4.8489; doublem3=0.000544;
//NotethatsalinityargumentisPracticalSalinity,thisis //beacusethemajorioniccomponentsofseawaterrelatedtoCl //arewhataffectthesolubilityofnon-electrolytesinseawater.
pt=gsw_pt_from_ct(SA,CT);//ptispotentialtemperaturereferencedtotheseasurface.
pt68=pt*1.00024;//pt68isthepotentialtemperatureindegressCon //the1968InternationalPracticalTemperatureScaleIPTS-68. y=pt68+gtc.gsw_t0; y_100=y*1e-2;
ph2odP=exp(m0-m1*100.0/y-m2*log(y_100)-m3*SP);//Moistaircorrectionat1atm.
N2Osol=(exp(a0+a1*100.0/y+a2*log(y_100)+a3*(y_100*y_100)+ SP*(b1+y_100*(b2+b3*y_100))))/(1.0-ph2odP);
returnN2Osol; }
/*************************************************************************** %gsw_Gibbs_energy_t_exactGibbsenergyofseawater %========================================================================== % %USAGE: %Gibbs_energy_t_exact=gsw_Gibbs_energy_t_exact(SA,t,p) % %DESCRIPTION: %CalculatestheGibbsenergyofseawater. % %INPUT: %SA=AbsoluteSalinity[g/kg] %t=in-situtemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) % %OUTPUT: %Gibbs_energy_t_exact=Gibbsenergy[J/kg] % %AUTHOR: %PaulBarkerandTrevorMcDougall[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org %Seesection2.13ofthisTEOS-10Manual. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_Gibbs_energy_t_exact(doubleSA,doublet,doublep) { returngsw_gibbs(0,0,0,SA,t,p); }
/*************************************************************************** %gsw_HesolsolubilityofHeinseawater %========================================================================== % %USAGE: %Hesol=gsw_Hesol(SA,CT,p,lon,lat) % %DESCRIPTION: %Calculatestheheliumconcentrationexpectedatequilibriumwithairat %anAbsolutePressureof101325Pa(seapressureof0dbar)including %saturatedwatervapor.Thisfunctionusesthesolubilitycoefficients %aslistedinWeiss(1971). % %NotethatthisalgorithmhasnotbeenapprovedbyIOCandisnotwork %fromSCOR/IAPSOWorkingGroup127.ItisincludedintheGSW %OceanographicToolboxasitseemstobeoceanographicbestpractice. % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) %lon=longitudeindecimaldegrees[0...+360] %or[-180...+180] %lat=latitudeindecimaldegreesnorth[-90...+90] % %OUTPUT: %Hesol=solubilityofheliuminmicro-molesperkg[umol/kg] % %AUTHOR:RobertaHamme,PaulBarkerandTrevorMcDougall %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %DymondandSmith,1980:Thevirialcoefficientsofpuregasesand %mixtures.ClarendonPress,Oxford. % %Weiss,R.F.,1971:SolubilityofHeliumandNeoninWaterandSeawater. %J.Chem.andEngineer.Data,16,235-241. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_Hesol(doubleSA,doubleCT,doublep,doublelon,doublelat) { doubleHe_ml2umol,Hesol,y,y_100,pt68,pt,SP=gsw_sp_from_sa(SA,p,lon,lat);
//NotethatsalinityargumentisPracticalSalinity,thisis //beacusethemajorioniccomponentsofseawaterrelatedtoCl //arewhataffectthesolubilityofnon-electrolytesinseawater.
pt=gsw_pt_from_ct(SA,CT);//ptispotentialtemperaturereferencedto //theseasurface.
pt68=pt*1.00024;//pt68isthepotentialtemperatureindegressCon //the1968InternationalPracticalTemperatureScaleIPTS-68.
y=pt68+gtc.gsw_t0; y_100=y*1e-2;
//ThecoefficentsbelowarefromTable3ofWeiss(1971) doublea1=-167.2178; doublea2=216.3442; doublea3=139.2032; doublea4=-22.6202; doubleb1=-0.044781; doubleb2=0.023541; doubleb3=-0.0034266;
Hesol=exp(a1+a2*100.0/y+a3*log(y_100)+a4*y_100+ SP*(b1+y_100*(b2+b3*y_100)));
He_ml2umol=4.455817671505537e1;//mL/kgtoumol/kgforHe(1/22.44257e-3) //MolarvolumeatSTP(DymondandSmith,1980). Hesol=Hesol*He_ml2umol; returnHesol; }
/*************************************************************************** %gsw_KrsolsolubilityofKrinseawater %========================================================================== % %USAGE: %gsw_Krsol(SA,CT,p,lon,lat) % %DESCRIPTION: %Calculatesthekrypton,Kr,concentrationexpectedatequilibriumwith %airatanAbsolutePressureof101325Pa(seapressureof0dbar) %includingsaturatedwatervapor.Thisfunctionusesthesolubility %coefficientsderivedfromthedataofWeissandKyser(1978). % %NotethatthisalgorithmhasnotbeenapprovedbyIOCandisnotwork %fromSCOR/IAPSOWorkingGroup127.ItisincludedintheGSW %OceanographicToolboxasitseemstobeoceanographicbestpractice. % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) %lon=longitudeindecimaldegrees[0...+360] %or[-180...+180] %lat=latitudeindecimaldegreesnorth[-90...+90] % %OUTPUT: %Krsol=solubilityofkryptoninmicro-molesperkg[umol/kg] % %AUTHOR:RobertaHamme,PaulBarkerandTrevorMcDougall %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %Weiss,R.F.,andT.K.Kyser,1978:SolubilityofKryptoninWaterand %Seawater.J.Chem.Thermodynamics,23,69-72. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_Krsol(doubleSA,doubleCT,doublep,doublelon,doublelat) { doubleKr_ml2umol,Krsol,pt,pt68,y,y_100,SP=gsw_sp_from_sa(SA,p,lon,lat);
//NotethatsalinityargumentisPracticalSalinity,thisis //beacusethemajorioniccomponentsofseawaterrelatedtoCl //arewhataffectthesolubilityofnon-electrolytesinseawater.
pt=gsw_pt_from_ct(SA,CT);//ptispotentialtemperaturereferencedto //theseasurface.
pt68=pt*1.00024;//pt68isthepotentialtemperatureindegressCon //the1968InternationalPracticalTemperatureScaleIPTS-68.
y=pt68+gtc.gsw_t0; y_100=y*1e-2;
//Table2(WeissandKyser,1978) doublea1=-112.6840; doublea2=153.5817; doublea3=74.4690; doublea4=-10.0189; doubleb1=-0.011213; doubleb2=-0.001844; doubleb3=0.0011201;
Krsol=exp(a1+a2*100/y+a3*log(y_100)+a4*y_100+ SP*(b1+y_100*(b2+b3*y_100)));
Kr_ml2umol=4.474052731185490e1;//mL/kgtoumol/kgforKr(1/22.3511e-3) //MolarvolumeatSTP(DymondandSmith,1980).
Krsol=Krsol*Kr_ml2umol;
returnKrsol; }
/*************************************************************************** %gsw_enthalpy_diff_CT_exactdifferenceofenthalpyattwopressures %========================================================================== % %USAGE: %gsw_enthalpy_diff_CT_exact(SA,CT,p_shallow,p_deep) % %DESCRIPTION: %Calculatesthedifferenceofthespecificenthalpyofseawaterbetween %twodifferentpressures,p_deep(thedeeperpressure)andp_shallow %(theshallowerpressure),atthesamevaluesofSAandCT.Theoutput %(enthalpy_diff_CT_exact)isthespecificenthalpyevaluatedat %(SA,CT,p_deep)minusthespecificenthalpyat(SA,CT,p_shallow). % %NotethatthisfunctionusesthefullGibbsfunction.Thereisan %alternativetocallingthisfunction,namelygsw_enthalpy_diff(SA,CT,p), %whichusesthecomputationallyefficient75-termexpressionforspecific %volumeintermsofSA,CTandp(Roquetetal.,2015). % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p_shallow=upperseapressure[dbar] %(i.e.shallowerabsolutepressure-10.1325dbar) %p_deep=lowerseapressure[dbar] %(i.e.deeperabsolutepressure-10.1325dbar) % %OUTPUT: %enthalpy_diff_CT_exact=differenceofspecificenthalpy[J/kg] %(deepminusshallow) % %AUTHOR: %TrevorMcDougallandPaulBarker[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org %SeeEqns.(3.32.2)ofthisTEOS-10Manual. % %McDougall,T.J.,2003:Potentialenthalpy:Aconservativeoceanic %variableforevaluatingheatcontentandheatfluxes.Journalof %PhysicalOceanography,33,945-963. %SeeEqns.(18)and(22) % %Roquet,F.,G.Madec,T.J.McDougall,P.M.Barker,2015:Accurate %polynomialexpressionsforthedensityandspecifcvolumeofseawater %usingtheTEOS-10standard.OceanModelling.,90,pp.29-43. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_enthalpy_diff_CT_exact( doubleSA,doubleCT, doublep_shallow, doublep_deep) { doublet_shallow=gsw_t_from_ct(SA,CT,p_shallow); doublet_deep=gsw_t_from_ct(SA,CT,p_deep);
returngsw_enthalpy_t_exact(SA,t_deep,p_deep)- gsw_enthalpy_t_exact(SA,t_shallow,p_shallow); }
/*************************************************************************** %gsw_ArsolsolubilityofArinseawater %========================================================================== % %USAGE: %gsw_Arsol(SA,CT,p,lon,lat) % %DESCRIPTION: %Calculatestheargon,Ar,concentrationexpectedatequilibriumwithair %atanAbsolutePressureof101325Pa(seapressureof0dbar)including %saturatedwatervapor.Thisfunctionusesthesolubilitycoefficients %aslistedinHammeandEmerson(2004). % %NotethatthisalgorithmhasnotbeenapprovedbyIOCandisnotwork %fromSCOR/IAPSOWorkingGroup127.ItisincludedintheGSW %OceanographicToolboxasitseemstobeoceanographicbestpractice. % %INPUT: %SA=AbsoluteSalinity[g/kg] %CT=ConservativeTemperature(ITS-90)[degC] %p=seapressure[dbar] %(i.e.absolutepressure-10.1325dbar) %lon=longitudeindecimaldegrees[0...+360] %or[-180...+180] %lat=latitudeindecimaldegreesnorth[-90...+90] % %OUTPUT: %Arsol=solubilityofargon[umol/kg] % %AUTHOR:RobertaHamme,PaulBarkerandTrevorMcDougall %[help@teos-10.org] % %VERSIONNUMBER:3.06.12(25thMay,2020) % %REFERENCES: %IOC,SCORandIAPSO,2010:Theinternationalthermodynamicequationof %seawater-2010:Calculationanduseofthermodynamicproperties. %IntergovernmentalOceanographicCommission,ManualsandGuidesNo.56, %UNESCO(English),196pp.Availablefromhttp://www.TEOS-10.org % %Hamme,R.,andS.Emerson,2004:Thesolubilityofneon,nitrogenand %argonindistilledwaterandseawater.Deep-SeaResearch,51, %1517-1528. % %Thesoftwareisavailablefromhttp://www.TEOS-10.org % %========================================================================== ***************************************************************************/ doubleTeosBase::gsw_Arsol(doubleSA,doubleCT,doublep,doublelon,doublelat) { doubley,pt,SP=gsw_sp_from_sa(SA,p,lon,lat);
pt=gsw_pt_from_ct(SA,CT); //ptispotentialtemperaturereferencedtotheseasurface.
y=log((298.15-pt)/(273.15+pt)); //ptisthetemperatureindegressConthe1990InternationalTemperatureScaleITS-90.
doublea0=2.79150; doublea1=3.17609; doublea2=4.13116; doublea3=4.90379; doubleb0=-6.96233e-3; doubleb1=-7.66670e-3; doubleb2=-1.16888e-2;
returnexp(a0+y*(a1+y*(a2+a3*y))+SP*(b0+y*(b1+b2*y))); }
/************************************** ====== method:gsw_rtIsNaN ====== returnstrueorfalsedependingon equalityornotofrtNaNwithval **************************************/ boolTeosBase::gsw_rtIsNaN(doubleval) { returnval==rtNaN; }
/************************************************************************************************* Functionsforpchipinterpolation (PiecewiseCubicHermiteInterpolatingPolynomial) basedon https://docs.scipy.org/doc/scipy/reference/generated/scipy.interpolate.PchipInterpolator.html# scipy.interpolate.PchipInterpolator Seereferencestherein. Thisisashape-preservingalgorithm,inthesensethatitdoesnot overshoottheoriginalpoints;extremaoftheinterpolatedcurvematch theextremaoftheoriginalpoints. ***************************************************************************************************/ intTeosBase::gsw_sgn(doublex) { return((x)>0.0)?1:(((x)<0)?-1:0); }
/************************************************************************** ======= method:gsw_pchip_derivs(*x,*y,n,*d) ======= Calculationofthederivativesisthekeytotheshape-preservation. Thereareotheralgorithmsthatcouldbeused,butthisappearstobe thesimplest,andadequateforourpurposes. Atminimalcomputationalcost,weincludehereacheckforincreasingx. Returns0onsuccess,1ifxisnotstrictlyincreasing. ****************************************************************************/ intTeosBase::gsw_pchip_derivs(double*x,double*y,intn,double*d) { doublemm,mp; doublehm,hp; intsmm,smp; doublew1,w2; inti; if(n==2){ d[0]=d[1]=(y[1]-y[0])/(x[1]-x[0]); return0; } hm=x[1]-x[0]; hp=x[2]-x[1]; mm=(y[1]-y[0])/hm; mp=(y[2]-y[1])/hp; d[0]=gsw_pchip_edge_case(hm,hp,mm,mp); smm=gsw_sgn(mm); smp=gsw_sgn(mp); for(i=1;i<n-1;i++){ if(hm<=0){ return1; } if((smm!=smp)||mp==0||mm==0){ d[i]=0.0; }else{ w1=2*hp+hm; w2=hp+2*hm; d[i]=(w1+w2)/(w1/mm+w2/mp); } if(i<n-2){ hm=hp; hp=x[i+2]-x[i+1]; mm=mp; mp=(y[i+2]-y[i+1])/hp; smm=smp; smp=gsw_sgn(mp); } } if(hp<=0){ return1; } d[n-1]=gsw_pchip_edge_case(hp,hm,mp,mm); return0; }
/************************************************************************** ======= method:gsw_pchip_edge_case(h0,h1,m0,m1) ======= ****************************************************************************/ doubleTeosBase::gsw_pchip_edge_case(doubleh0,doubleh1,doublem0,doublem1) { doubled; intmask,mask2; d=((2*h0+h1)*m0-h0*m1)/(h0+h1); mask=gsw_sgn(d)!=gsw_sgn(m0); mask2=(gsw_sgn(m0)!=gsw_sgn(m1))&&(fabs(d)>3.0*fabs(m0)); if(mask){ return0.0; } if(!mask&&mask2){ return3.0*m0; } returnd; }
/************************************************************************* ========================================================================== method:gsw_add_barrier(input_data,lon,lat,long_grid,lat_grid,dlong_grid, dlat_grid,output_data) ========================================================================== AddsabarrierthroughCentralAmerica(Panama)andthenaverages overtheappropriatesideofthebarrier data_in:data[unitless] lon:Longitudesofdatadegreeseast[0...+360] lat:Latitudesofdatadegreesnorth[-90...+90] longs_grid:Longitudesofregulargriddegreeseast[0...+360] lats_grid:Latitudesofregulargriddegreesnorth[-90...+90] dlongs_grid:Longitudedifferenceofregulargriddegrees[deglongitude] dlats_grid:Latitudedifferenceofregulargriddegrees[deglatitude] output_data:averageofdatadependingonwhichsideofthe Panamacanalitison[unitless] **************************************************************************/ voidTeosBase::gsw_add_barrier( double*input_data,doublelon,doublelat, doublelong_grid,doublelat_grid,doubledlong_grid, doubledlat_grid,double*output_data) { intabove_line[4],k,nmean,above_line0,kk; doubler,lats_line,data_mean;
k=gsw_util_indx(gsaar.longs_pan,gsaar.npan,lon); r=(lon-gsaar.longs_pan[k])/(gsaar.longs_pan[k+1]-gsaar.longs_pan[k]); lats_line=gsaar.lats_pan[k]+r*(gsaar.lats_pan[k+1]-gsaar.lats_pan[k]); above_line0=(lats_line<=lat); k=gsw_util_indx(gsaar.longs_pan,gsaar.npan,long_grid); r=(long_grid-gsaar.longs_pan[k])/ (gsaar.longs_pan[k+1]-gsaar.longs_pan[k]); lats_line=gsaar.lats_pan[k]+r*(gsaar.lats_pan[k+1]-gsaar.lats_pan[k]); above_line[0]=(lats_line<=lat_grid); above_line[3]=(lats_line<=lat_grid+dlat_grid); k=gsw_util_indx(gsaar.longs_pan,6,long_grid+dlong_grid); r=(long_grid+dlong_grid-gsaar.longs_pan[k])/ (gsaar.longs_pan[k+1]-gsaar.longs_pan[k]); lats_line=gsaar.lats_pan[k]+r*(gsaar.lats_pan[k+1]-gsaar.lats_pan[k]); above_line[1]=(lats_line<=lat_grid); above_line[2]=(lats_line<=lat_grid+dlat_grid); nmean=0; data_mean=0.0; for(kk=0;kk<4;kk++){ if((fabs(input_data[kk])<=100.0)&&above_line0==above_line[kk]){ nmean=nmean+1; data_mean=data_mean+input_data[kk]; } } if(nmean==0){ data_mean=0.0; }else{ data_mean=data_mean/nmean; } for(kk=0;kk<4;kk++){ if((fabs(input_data[kk])>=1.0e10)||above_line0!=above_line[kk]){ output_data[kk]=data_mean; }else{ output_data[kk]=input_data[kk]; } } }
/************************************************************************* ========================================================================== method:gsw_add_mean(data_in,data_out) ========================================================================== ReplacesNaNswithnon-nanmeanofthe4adjacentneighbours data_in:datasetofthe4adjacentneighbours[double] data_out:non-nanmeanofthe4adjacentneighbours[unitless] *************************************************************************/ voidTeosBase::gsw_add_mean(double*data_in,double*data_out) { intk,nmean=0; doubledata_mean=0.0; for(k=0;k<4;k++){ if(fabs(data_in[k])<=100.0){ nmean++; data_mean=data_mean+data_in[k]; } } if(nmean==0.0){ data_mean=0.0; }else{ data_mean=data_mean/nmean; } for(k=0;k<4;k++){ if(fabs(data_in[k])>=100.0){ data_out[k]=data_mean; }else{ data_out[k]=data_in[k]; } } }
/*************************************************************************** ======= method:gsw_rr68_interp_section(sectnum,ip_sect,ip_isect,sa_i,ct_i) ======= ****************************************************************************/ voidTeosBase::gsw_rr68_interp_section( intsectnum,double*sa,double*ct,double*p, intmp,intnsect,double*ip_sect,int*ip_isect, double*p_i,double*sa_i,double*ct_i) { inti,*ip_1=NULL,*ip_2=NULL,*ip_3=NULL,*ip_4=NULL;
double*ct_12=NULL,*ct_13=NULL,*ct_23=NULL,*ct_34=NULL, *ct_ref=NULL,*sa_12=NULL,*sa_13=NULL,*sa_23=NULL, *sa_34=NULL,*sa_ref=NULL,*p_ii=NULL;
doublem,ctp1,ctp2,ctref_denom,ct_ref_minus_ctp1,ct_ref_minus_ctp2, ctref_num,gamma1_23,gamma1_24,gamma2_31,gamma2_34,saref_num, gamma2_41,gamma3_12,gamma3_42,gamma4_12,gamma4_23, sap1,sap2,saref_denom,sa_ref_minus_sap1,sa_ref_minus_sap2;
ip_1=newint[4*nsect]; ip_2=ip_1+nsect; ip_3=ip_2+nsect; ip_4=ip_3+nsect;
ct_12=newdouble[12*nsect]; sa_12=ct_12+1*nsect; sa_13=ct_12+2*nsect; sa_23=ct_12+3*nsect; sa_34=ct_12+4*nsect; sa_ref=ct_12+5*nsect; ct_13=ct_12+6*nsect; ct_23=ct_12+7*nsect; ct_34=ct_12+8*nsect; ct_ref=ct_12+9*nsect; p_ii=ct_12+10*nsect;
if(sectnum<0){ for(i=0;i<nsect;i++){ ip_1[i]=floor(ip_sect[i]); ip_2[i]=ceil(ip_sect[i]); if(ip_1[i]==ip_2[i]){ ip_2[i]=ip_1[i]+1; } ip_3[i]=ip_2[i]+1; ip_4[i]=ip_3[i]+1; } }elseif(sectnum==0){ for(i=0;i<nsect;i++){ ip_2[i]=floor(ip_sect[i]); ip_3[i]=ceil(ip_sect[i]); if(ip_2[i]==ip_3[i]){ ip_2[i]=ip_3[i]-1; } ip_1[i]=ip_2[i]-1; if(ip_1[i]<0){ ip_1[i]=0; ip_2[i]=1; ip_3[i]=2; } ip_4[i]=ip_3[i]+1; } }elseif(sectnum>0){ for(i=0;i<nsect;i++){ ip_1[i]=ceil(ip_sect[i]); ip_2[i]=floor(ip_sect[i]); if(ip_1[i]==ip_2[i]){ ip_2[i]=ip_1[i]-1; } ip_3[i]=ip_2[i]-1; ip_4[i]=ip_3[i]-1; } }
for(i=0;i<nsect;i++){ p_ii[i]=p_i[ip_isect[i]]; }
for(i=0;i<nsect;i++){ sa_34[i]=sa[ip_3[i]]+((sa[ip_4[i]]-sa[ip_3[i]])* (p_ii[i]-p[ip_3[i]])/(p[ip_4[i]]-p[ip_3[i]])); ct_34[i]=ct[ip_3[i]]+((ct[ip_4[i]]-ct[ip_3[i]])* (p_ii[i]-p[ip_3[i]])/(p[ip_4[i]]-p[ip_3[i]])); } m=1.7; if(sectnum==0){ gsw_linear_interp_sa_ct(sa,ct,p,mp,p_ii,nsect,sa_23,ct_23); for(i=0;i<nsect;i++){ sa_12[i]=sa[ip_1[i]]+((sa[ip_2[i]]-sa[ip_1[i]])* (p_ii[i]-p[ip_1[i]])/(p[ip_2[i]]-p[ip_1[i]]));
ct_12[i]=ct[ip_1[i]]+((ct[ip_2[i]]-ct[ip_1[i]])* (p_ii[i]-p[ip_1[i]])/(p[ip_2[i]]-p[ip_1[i]]));
saref_num=(pow(fabs(sa_23[i]-sa_34[i]),m))*sa_12[i]+ (pow(fabs(sa_12[i]-sa_23[i]),m))*sa_34[i];
ctref_num=(pow(fabs(ct_23[i]-ct_34[i]),m))*ct_12[i]+ (pow(fabs(ct_12[i]-ct_23[i]),m))*ct_34[i];
saref_denom=pow(fabs(sa_23[i]-sa_34[i]),m)+ pow(fabs(sa_12[i]-sa_23[i]),m);
ctref_denom=pow(fabs(ct_23[i]-ct_34[i]),m)+ pow(fabs(ct_12[i]-ct_23[i]),m);
if(saref_denom==0.0){ sa_23[i]=sa_23[i]+1.0e-6; saref_num=(pow(fabs(sa_23[i]-sa_34[i]),m))*sa_12[i]+ (pow(fabs(sa_12[i]-sa_23[i]),m))*sa_34[i];
saref_denom=pow(fabs(sa_23[i]-sa_34[i]),m)+ pow(fabs(sa_12[i]-sa_23[i]),m); } if(ctref_denom==0.0){ ct_23[i]=ct_23[i]+1.0e-6;
ctref_num=(pow(fabs(ct_23[i]-ct_34[i]),m))*ct_12[i]+ (pow(fabs(ct_12[i]-ct_23[i]),m))*ct_34[i];
ctref_denom=pow(fabs(ct_23[i]-ct_34[i]),m)+ pow(fabs(ct_12[i]-ct_23[i]),m); }
sa_ref[i]=0.5*(sa_23[i]+(saref_num/saref_denom)); ct_ref[i]=0.5*(ct_23[i]+(ctref_num/ctref_denom)); } }else{ gsw_linear_interp_sa_ct(sa,ct,p,mp,p_ii,nsect,sa_12,ct_12); for(i=0;i<nsect;i++){ sa_13[i]=sa[ip_1[i]]+((sa[ip_3[i]]-sa[ip_1[i]])* (p_ii[i]-p[ip_1[i]])/(p[ip_3[i]]-p[ip_1[i]]));
ct_13[i]=ct[ip_1[i]]+((ct[ip_3[i]]-ct[ip_1[i]])* (p_ii[i]-p[ip_1[i]])/(p[ip_3[i]]-p[ip_1[i]]));
sa_23[i]=sa[ip_2[i]]+((sa[ip_3[i]]-sa[ip_2[i]])* (p_ii[i]-p[ip_2[i]])/(p[ip_3[i]]-p[ip_2[i]]));
ct_23[i]=ct[ip_2[i]]+((ct[ip_3[i]]-ct[ip_2[i]])* (p_ii[i]-p[ip_2[i]])/(p[ip_3[i]]-p[ip_2[i]]));
saref_num=(pow(fabs(sa_12[i]-sa_23[i]),m))*sa_34[i]+ (pow(fabs(sa_12[i]-sa_13[i]),m))*sa_23[i];
ctref_num=(pow(fabs(ct_12[i]-ct_23[i]),m))*ct_34[i]+ (pow(fabs(ct_12[i]-ct_13[i]),m))*ct_23[i];
saref_denom=pow(fabs(sa_12[i]-sa_23[i]),m)+ pow(fabs(sa_12[i]-sa_13[i]),m);
ctref_denom=pow(fabs(ct_12[i]-ct_23[i]),m)+ pow(fabs(ct_12[i]-ct_13[i]),m);
if(saref_denom==0.0){ sa_23[i]=sa_23[i]+1.0e-6;
saref_num=(pow(fabs(sa_12[i]-sa_23[i]),m))*sa_34[i]+ (pow(fabs(sa_12[i]-sa_13[i]),m))*sa_23[i];
saref_denom=pow(fabs(sa_12[i]-sa_23[i]),m)+ pow(fabs(sa_12[i]-sa_13[i]),m); } if(ctref_denom==0.0){ ct_23[i]=ct_23[i]+1.0e-6;
ctref_num=(pow(fabs(ct_12[i]-ct_23[i]),m))*ct_34[i]+ (pow(fabs(ct_12[i]-ct_13[i]),m))*ct_23[i];
ctref_denom=pow(fabs(ct_12[i]-ct_23[i]),m)+ pow(fabs(ct_12[i]-ct_13[i]),m); }
sa_ref[i]=0.5*(sa_12[i]+(saref_num/saref_denom)); ct_ref[i]=0.5*(ct_12[i]+(ctref_num/ctref_denom)); } }
for(i=0;i<nsect;i++){ gamma1_23=((p_ii[i]-p[ip_2[i]])*(p_ii[i]-p[ip_3[i]]))/ ((p[ip_1[i]]-p[ip_2[i]])*(p[ip_1[i]]-p[ip_3[i]]));
gamma2_31=((p_ii[i]-p[ip_3[i]])*(p_ii[i]-p[ip_1[i]]))/ ((p[ip_2[i]]-p[ip_3[i]])*(p[ip_2[i]]-p[ip_1[i]]));
gamma3_12=((p_ii[i]-p[ip_1[i]])*(p_ii[i]-p[ip_2[i]]))/ ((p[ip_3[i]]-p[ip_1[i]])*(p[ip_3[i]]-p[ip_2[i]])); if(sectnum==0){ gamma2_34=((p_ii[i]-p[ip_3[i]])*(p_ii[i]-p[ip_4[i]]))/ ((p[ip_2[i]]-p[ip_3[i]])*(p[ip_2[i]]-p[ip_4[i]]));
gamma3_42=((p_ii[i]-p[ip_4[i]])*(p_ii[i]-p[ip_2[i]]))/ ((p[ip_3[i]]-p[ip_4[i]])*(p[ip_3[i]]-p[ip_2[i]]));
gamma4_23=((p_ii[i]-p[ip_2[i]])*(p_ii[i]-p[ip_3[i]]))/ ((p[ip_4[i]]-p[ip_2[i]])*(p[ip_4[i]]-p[ip_3[i]])); }else{ gamma1_24=((p_ii[i]-p[ip_2[i]])*(p_ii[i]-p[ip_4[i]]))/ ((p[ip_1[i]]-p[ip_2[i]])*(p[ip_1[i]]-p[ip_4[i]]));
gamma2_41=((p_ii[i]-p[ip_4[i]])*(p_ii[i]-p[ip_1[i]]))/ ((p[ip_2[i]]-p[ip_4[i]])*(p[ip_2[i]]-p[ip_1[i]]));
gamma4_12=((p_ii[i]-p[ip_1[i]])*(p_ii[i]-p[ip_2[i]]))/ ((p[ip_4[i]]-p[ip_1[i]])*(p[ip_4[i]]-p[ip_2[i]])); } sap1=gamma1_23*sa[ip_1[i]]+gamma2_31*sa[ip_2[i]]+ gamma3_12*sa[ip_3[i]];
ctp1=gamma1_23*ct[ip_1[i]]+gamma2_31*ct[ip_2[i]]+ gamma3_12*ct[ip_3[i]];
if(sectnum==0){ sap2=gamma2_34*sa[ip_2[i]]+gamma3_42*sa[ip_3[i]]+ gamma4_23*sa[ip_4[i]];
ctp2=gamma2_34*ct[ip_2[i]]+gamma3_42*ct[ip_3[i]]+ gamma4_23*ct[ip_4[i]]; }else{ sap2=gamma1_24*sa[ip_1[i]]+gamma2_41*sa[ip_2[i]]+ gamma4_12*sa[ip_4[i]];
ctp2=gamma1_24*ct[ip_1[i]]+gamma2_41*ct[ip_2[i]]+ gamma4_12*ct[ip_4[i]]; } sa_ref_minus_sap1=fabs(sa_ref[i]-sap1); sa_ref_minus_sap2=fabs(sa_ref[i]-sap2);
if(sa_ref_minus_sap1==0.0&&sa_ref_minus_sap2==0.0){ sa_ref[i]=sa_ref[i]+1.0e-6; sa_ref_minus_sap1=fabs(sa_ref[i]-sap1); sa_ref_minus_sap2=fabs(sa_ref[i]-sap2); }
ct_ref_minus_ctp1=fabs(ct_ref[i]-ctp1); ct_ref_minus_ctp2=fabs(ct_ref[i]-ctp2);
if(ct_ref_minus_ctp1==0.0&&ct_ref_minus_ctp2==0.0){ ct_ref[i]=ct_ref[i]+1.0e-6; ct_ref_minus_ctp1=fabs(ct_ref[i]-ctp1); ct_ref_minus_ctp2=fabs(ct_ref[i]-ctp2); }
sa_i[ip_isect[i]]=(sa_ref_minus_sap1*sap2+sa_ref_minus_sap2*sap1)/ (sa_ref_minus_sap1+sa_ref_minus_sap2);
ct_i[ip_isect[i]]=(ct_ref_minus_ctp1*ctp2+ct_ref_minus_ctp2*ctp1)/ (ct_ref_minus_ctp1+ct_ref_minus_ctp2); }
if(ct_12){delete[]ct_12;} if(ip_1){delete[]ip_1;} }
/************************************************************************** ========================================================================== method:gsw_sum(*x,n) ========================================================================== *************************************************************************/ doubleTeosBase::gsw_sum(double*x,intn) { inti; doubleval;
for(val=0.0,i=0;i<n;val+=x[i],i++){ }
returnval; }
/************************************************************************** ========================================================================== method:gsw_util_indx(x,n,z,k) ========================================================================== Findstheindexofthevalueinamonotonicallyincreasingarray x:arrayofmonotonicallyincreasingvalues n:lengthofthearray z:valuetobeindexed K:indexK-ifX(K)<=Z<X(K+1),or N-1-ifZ=X(N) *************************************************************************/ intTeosBase::gsw_util_indx(double*x,intn,doublez) { intk,ku,kl,km; if(z>x[0]&&z<x[n-1]){ kl=0; ku=n-1; while(ku-kl>1){ km=(ku+kl)>>1; if(z>x[km]){ kl=km; }else{ ku=km; } } k=kl; if(z==x[k+1]){ k++; } }elseif(z<=x[0]){ k=0; }else{ k=n-2; } returnk; }
/*********************************************************** ======= method:gsw_util_sort_dbl =======
Thedefaultvalueforparameter"ASC"is"false" (seeTeosBase.h)whichmeansthat"orderBySmallToLarge" iscalledby"sort"(dArray[0]settosmallestvalue). Whenparameter"ASC"is"true""orderByLargeToSmall"is calledby"sort"(dArray[0]settolargestvalue). ------------- Theexerciseistoplacethe"dArray"and"iArray" intoalistof"structDI"(seeTeosBase.h)objects. ThatlististhenorderedbythevaluesinthedArray accordingtothesequencesetting("ASC").Thesequence beginsatdArray[0]andproceedstothefinalvalue. ************************************************************/ voidTeosBase::gsw_util_sort_dbl(double*dArray,intnx,int*iArray,boolASC) { list<DI>*pDI=newlist<DI>; inti; for(i=0;i<nx;i++){ DIdi; di.i=iArray[i]; di.d=dArray[i]; pDI->push_back(di); } if(ASC){ pDI->sort(orderByLargeToSmall); }else{ pDI->sort(orderBySmallToLarge); } list<DI>::const_iteratoritr=pDI->begin(); i=0; while(itr!=pDI->end()){ iArray[i]=itr->i; dArray[i]=itr->d; ++i; ++itr; } if(pDI){ pDI->empty(); deletepDI; } }
